fn u64_4_to_u8_32(
    array: [u64; 4]
) -> [u8; 32] {
    let mut output: [u8; 32] = [0; 32];
    let mut output_index = 0;

    for num_idx in 0..4 {
        for bit_pos in 0..8 {
            let shift_amount: u64 = 56 - (bit_pos * 8) as u64;
            output[output_index] = ((array[num_idx] >> shift_amount as u8) & 255) as u8;
            output_index += 1;
        }
    }

    output
}

unconstrained
