#[test]
fn test_split_u8_64() {
    let u8_64 = [
        193,255,211,207,238,45,158,92,214,118,67,248,243,159,214,229,26,173,136,246,244,206,106,184,130,114,121,207,255,185,34,102,
        193,255,211,207,238,45,158,92,214,118,67,248,243,159,214,229,26,173,136,246,244,206,106,184,130,114,121,207,255,185,34,102
    ];

    let (arr_a, arr_b) = split_u8_64(u8_64);

    for i in 0..32 {
